mapper.insert:
insert into boardinfo (
	boardseq
	, id
	, boardtitle
	, boardcontent
	, hitcount
	, indate
) values(
	boardseq.nextval
	, #{id}
	, #{boardtitle}
	, #{boardcontent}
	, #{hitcount}
	, sysdate
)

mapper.update:
update boardinfo
	set
		boardtitle = #{boardtitle},
		boardcontent = #{boardcontent}
	where
		boardseq = #{boardseq}
		and
		id = #{id}

mapper.insert:
INSERT INTO comments
(
	commentseq
	, boardseq
	, id
	, comments
	, indate
) VALUES (
	commentseq.nextval
	, #{boardseq}
	, #{id}
	, #{comments}
	, sysdate
)
