mapper.insert:
		INSERT INTO
			menu_info (
				menu_seq
			    , menu_type
			    , menu_name
			    , menu_description
			    , original_price
			    , promotion_price
			    , promotion_type
			    , release_date
			    , image
			) VALUES (
				menu_seq.NEXTVAL
    			, #{menu_type}
				, #{menu_name}
				, #{menu_description}
				, #{original_price}				
				, #{promotion_price}
				, #{promotion_type}
    			, SYSDATE
    			, #{image}
			)

mapper.update:
		UPDATE menu_info
		SET
			promotion_price = #{promotion_price}
		    , promotion_type = #{promotion_type}
    	WHERE
    		menu_seq = #{menu_seq}
